import {useEffect, useState } from 'react';
import './App.css';
import CreateCategoryCard from './components/functions/cerateCategoryCard';
import SearchInput from './components/css/searchInput'; 
function App() {

  function ShowCategoryDetails(catName){
    alert(catName+' category clicked')
  }

  const [categories, setCategories] = useState([]);
  useEffect(()=> {
    fetch('/api')
      .then(res => res.json())
      .then(data => {
        setCategories(data)
      })
      },[])

  return (
    console.clear(),
    <div className='App'>
            <SearchInput>S</SearchInput> 

      <div className='App-header'>
      <h3>Nitto Shop</h3>
      </div>
      <div className='categoryContainer'>
        {categories.map((category,index)=>
        <CreateCategoryCard key={index} doClick={ShowCategoryDetails} catObject={category}/>
        )}
      </div>
    </div>
  );
}

export default App;
