import { Routes, Route } from "react-router-dom";
import "./App.css";
import Footer from "./components/functions/footer";
import Home from "./Pages/Home";
import ProductsDetails from "./Pages/productsDetail";
import Header from "./components/functions/header";
import Login from "./Pages/login";
import * as firebase from 'firebase/app';

const imagesPath = "https://nimahabibi.de/shop/image/";

const firebaseConfig = {
  apiKey: "AIzaSyAvSPs7BVhjigh3sfdqJoNmLHxp9ZN1VwE",
  authDomain: "nittoshop-3c4e4.firebaseapp.com",
  projectId: "nittoshop-3c4e4",
  storageBucket: "nittoshop-3c4e4.appspot.com",
  messagingSenderId: "935064541626",
  appId: "1:935064541626:web:1883c0c6831891a4e3e583",
  measurementId: "G-MPX3LT1SCG",
};


function App() {
  firebase.initializeApp(firebaseConfig)
  console.clear();

  return (
    <div className="App">
      <Header />
      <main id="main">
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="product/:id" element={<ProductsDetails />} />
          <Route path="login" element={<Login />} />
        </Routes>
      </main>
      <Footer className="footer" />
    </div>
  );
}

export default App;
