      <h3>Nitto Shop</h3>
      </div>
      <div className='categoryContainer'>
        {categories.map((category,index)=>
        <CreateCategoryCard key={index} doClick={ShowCategoryDetails} catObject={category}/>
        )}
      </div>
    </div>

  );
}

export default App;
