import styled from "styled-components"



const CreateLoginPage=()=>{
let LoginDisplay="flex";
const ToggleLoginRegister=()=>{
LoginDisplay = LoginDisplay=="flex"?"none":"flex"
}
    return(
        <>
        <head>
        <link href="https://fonts.googleapis.com/css2?family=Bebas+Neue&family=Roboto:wght@300&display=swap" rel="stylesheet"></link>
        </head>
<LoginFormDiv>
    <Label>Login here to access your Profile</Label>
<UsernameTextBox type="text" placeholder="enter your username here"></UsernameTextBox>
<UsernameTextBox type="password" placeholder="enter your password here"></UsernameTextBox>
<p className="accountRegisterP">if you have not registered yet, <a href="" onClick={ToggleLoginRegister}>Click Here</a>  to setup your access</p>
</LoginFormDiv>
</>
    )
}
export default CreateLoginPage


const LoginFormDiv=styled.div`
display:flex;
flex-direction:column;
justify-content:center;
width:100%;
height:100%;
`;

const UsernameTextBox=styled.input`
margin:20px auto;
font-size:1rem;
width:300px;
height:50px;
`;

const Label=styled.label`
margin:20px auto;
align-self:center;
font-size:1.5rem;
font-weight:bold;
font-family:'Roboto',cursive;
color:#063773;
`;
